    const {data} = await axios.get("https://api.spotify.com/v1/me/top/tracks",{
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    setItems(data.items)
    /*const arr = data.items
    var tracksArr = []
    for (let i = 0; i < arr.length; i++){
      tracksArr.push(arr[i].name)
