import React from 'react';
import axios from 'axios';
import { useEffect, useState } from "react";
import TopArtistsBoard from './TopArtistsBoard'
import TopTracksBoard from './TopTracksBoard';

const App = () => {

  const CLIENT_ID = "ade964553b5b452bb412b514e669c3b0"
  const REDIRECT_URI = "http://localhost:3000"
  const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize"
  const RESPONSE_TYPE = "token"
  const SCOPE = "user-top-read"

  const [token, setToken] = useState("");
  const [items, setItems] = useState([])
  const [name, setName] = useState("");
  const [nResults, setNResults] = useState(20)
  const [timeRange, setTimeRange] = useState("medium_term")


  useEffect(() => {
    const hash = window.location.hash
    let token = window.localStorage.getItem("token")


    if (!token && hash) {
        token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]

        window.location.hash = ""
        window.localStorage.setItem("token", token)
    }
    //console.log(token)
    setToken(token)
    //getDisplayName()
  }, [])


  const logout = () => {
    setToken("")
    window.localStorage.removeItem("token")
  }

  const getDisplayName = async (e) => {
    const {data} = await axios.get("https://api.spotify.com/v1/me",{
      headers: {
        Authorization: `Bearer ${token}`
      }
    })

    setName(data.display_name)
  }

  const getTopArtists = async (e) => {
    e.preventDefault()
    const {data} = await axios.get("https://api.spotify.com/v1/me/top/artists",{
      headers: {
        Authorization: `Bearer ${token}`
      },
      params:{
        limit: nResults,
        time_range: timeRange
      }
    })
    setItems(data.items)

  }

  const getTopTracks = async (e) => {
    e.preventDefault()
    const {data} = await axios.get("https://api.spotify.com/v1/me/top/tracks",{
      headers: {
        Authorization: `Bearer ${token}`
      },
      params:{
        limit: nResults,
        time_range: timeRange
      }
    })
    setItems(data.items)
  }

  return ( 
    <div>
      <h1>rankYourWrapped</h1>

      <div className="filters_container">
        <label for="n_results_input"># of Results</label>
        <input id="n_results_input" type="number" min="1" max="50" placeholder='20'onChange={e => setNResults(e.target.value)}></input>
        <label for="time_range_select">Time Range</label>
        <select id="time_range_select" onChange={e => setTimeRange(e.target.value)}>
          <option value="short_term"> 4 Weeks </option>
          <option value="medium_term" defaultValue> 6 Months </option>
          <option value="long_term"> All Time </option>
        </select>
      </div>



      {token ?

        <div>

          <form onSubmit={getTopArtists}>
          <button type={"submit"}>Get Artists</button>

          </form>
          <form onSubmit={getTopTracks}>
          <button type={"submit"}>Get Tracks</button>

          </form>
        </div>
        : <h2>Please Login</h2>

      }


      <TopArtistsBoard items={items} name={name}/>
      {!token ? 
        <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=${SCOPE}`}>Login
                        to Spotify</a>
                    : <button onClick={logout}>Logout</button>}

    </div>

  );
}

export default App;
